<?php

namespace backend\noJs\pages;

use backend\Configs;
use backend\Main;
use backend\exceptions\PageFlowException;
use Exception;
use backend\noJs\Page;
use backend\noJs\Lang;

class About implements Page {
	function getTitle(): string {
		return Lang::get('about_esmira');
	}
	
	function getContent(): string {
		if($_SERVER['HTTP_HOST'] == Configs::get('url_about_esmira_host'))
			$url = '.' .Configs::get('url_about_esmira_json_location');
		else
			$url = 'https://'.Configs::get('url_about_esmira_host').Configs::get('url_about_esmira_json_location');
		
		function getContent($url) {
			$context = stream_context_create(array('http' => array('header'=>'Connection: close\r\n')));
			return file_get_contents($url, false, $context);
		}
		
		$jsonString = getContent(sprintf($url, Main::getLang('en')));
		if(!$jsonString) {
			$jsonString = getContent(sprintf($url, 'en'));
			
			if(!$jsonString)
				throw new PageFlowException('Could not load data');
		}
		
		$content = json_decode($jsonString);
		
		$output = "<div class=\"center\">
			<p class=\"justify lineSize\">$content->about_text</p>
			<a href=\"https://play.google.com/store/apps/details?id=at.jodlidev.esmira\" target=\"_blank\"><img alt=\"Android\" src=\"data:image/png;base64,\"></a>
			<a href=\"https://apps.apple.com/gb/app/esmira/id1538774594\" target=\"_blank\"><img alt=\"iOS\" src=\"data:image/png;base64,\"></a>
		</div>
		<br/>
		<br/>";
	
	
	
		foreach($content->page_about as $entry) {
			$output .= "<div class=\"titleRow\">$entry->title</div>";
			
			foreach($entry->elements as $element) {
				$output .= "<div class=\"highlight\">
					<div class=\"horizontal\">$element->icon</div>
					<span class=\"middle\">$element->title</span>
				</div>
				<div class=\"justify lineSize spacingLeft\">
					<p class=\"justify\">$element->desc</p>
				</div>";
			}
		}
		
		$output .= "<br/>
			<hr/>
			<br/>
			<br/>
			
			<div class=\"highlight\">
				<div class=\"horizontal\">
					<svg xmlns=\"http://www.w3.org/2000/svg\" height=\"48\" viewBox=\"0 0 24 24\" width=\"48\"><path d=\"M0 0h24v24H0z\" fill=\"none\"/><path d=\"M11.99 2C6.47 2 2 6.48 2 12s4.47 10 9.99 10C17.52 22 22 17.52 22 12S17.52 2 11.99 2zM12 20c-4.42 0-8-3.58-8-8s3.58-8 8-8 8 3.58 8 8-3.58 8-8 8zm3.5-9c.83 0 1.5-.67 1.5-1.5S16.33 8 15.5 8 14 8.67 14 9.5s.67 1.5 1.5 1.5zm-7 0c.83 0 1.5-.67 1.5-1.5S9.33 8 8.5 8 7 8.67 7 9.5 7.67 11 8.5 11zm3.5 6.5c2.33 0 4.31-1.46 5.11-3.5H6.89c.8 2.04 2.78 3.5 5.11 3.5z\"/></svg>
				</div>
				<span class=\"middle\">$content->use_for_own_studies</span>
			</div>
			<div class=\"justify lineSize spacingLeft\">
				<a class=\"justify\" target=\"_blank\" href=\"$content->repository_link'\">
				<p class=\"justify\">$content->esmira_own_server_get_started</p>
				</a>
			</div>";
		
		return $output;
	}
}